    @Test
    //   @Sql(scripts = {"classpath:sql/createCoreInitialData.sql"})
    public void testRegulatoryEnquiryASuccess() throws Exception {
        mockServer.expect(requestTo("http://localhost:8080/cis/customer/customer-core-inquiry-by-ucn/v1")).andExpect(method(HttpMethod.POST)).andRespond(withSuccess(successZosString(), MediaType.APPLICATION_JSON));
        MvcResult mvcResult = mvc.perform(MockMvcRequestBuilders.post(uri).contentType(MediaType.APPLICATION_JSON_UTF8_VALUE).header("X-Request-ID", "CUSTOMER").header("X-Originating-Consumer-Username", "CUSTOMER").header("X-Consumer-Username", "CUSTOMER").header("X-Correlation-ID", "CUSTOMER").header("Reply-To", "CUSTOMER").accept(MediaType.APPLICATION_JSON).content(objectMapper.writeValueAsString(buildCis1362FRequestWapper()))).andDo(MockMvcResultHandlers.print()).andReturn();
        int status = mvcResult.getResponse().getStatus();
       // assertEquals(200, status);
    }


